join.Person:
\
     function map(p, context) { \
       //console.log('p: ' + JSON.stringify(p)); \
       //console.log('p.friend: ' + JSON.stringify(p.friend)); \
       //console.log('context: ' + JSON.stringify(context)); \
       if (!context) { \
         if (p.friend) { \
           jsondb.lookup({'index':'_uuid', 'value': p.friend, 'objectType': 'Person'}, { person: p }); \
         }; \
         jsondb.lookup({'index':'friend', 'value': p._uuid, 'objectType': 'Person'}, { friend: p }); \
       } else { \
         if (context.friend) \
            jsondb.emit({name: p.name, friend: p.friend, foaf: context.friend.friend }); \
         else \
            jsondb.emit({name: context.person.name, friend: context.person.friend, foaf: p.friend }); \
       } \
     }
